mapper.sql:
FROM codeGroup a
WHERE 1=1
	AND a.codeGroupDelNy = #{voDelNy}


	AND a.codeGroupRegDate BETWEEN #{voDateStart} AND #{voDateEnd}
	AND a.codeGroupModDate BETWEEN #{voDateStart} AND #{voDateEnd}



	AND a.codeGroupSeq = #{voValue}
	AND a.codeGroupName LIKE CONCAT('%',#{voValue},'%')

mapper.insert:
insert into codeGroup(
codeGroupName
,codeGroupDelNy
)values(
#{codeGroupName}
,0
);



	select last_insert_id()

mapper.delete:
Delete from codeGroup
where 1=1
and codeGroupSeq = #{codeGroupSeq}
