mapper.select:
select a.codeGroupSeqF ,b.codeGroupName as xcodeGroupName ,a.codeDelNy ,a.codeSeq ,a.codeName ,a.codeRegDate ,a.codeModDate From code a join codeGroup b on a.codeGroupSeqF = b.codeGroupSeq WHERE 1=1 AND a.codeDelNy = #{voDelNy}
AND a.codeRegDate BETWEEN #{voDateStart} AND #{voDateEnd} AND a.codeModDate BETWEEN #{voDateStart} AND #{voDateEnd}
AND a.codeSeq = #{voValue} AND a.codeName LIKE CONCAT('%',#{voValue},'%')

mapper.insert:
insert into code(
	codeName
    ,codeDelNy
    ,codeGroupSeqF
   )values(
	    #{codeName}
	    ,0
	    ,#{codeGroupSeqF}
    )


	select last_insert_id()

mapper.update:
UPDATE code
SET
codeName=#{codeName}
,codeGroupSeqF=#{codeGroupSeqF}
where 1=1
	and codeSeq=#{codeSeq}

mapper.delete:
delete From code
	where 1=1
and codeSeq=#{codeSeq}
