mapper.select:
select memberSeq ,memberID ,memberPwd ,memberName ,memberBirthday ,memberEmail ,memberDpt ,memberDelNy ,memberGenderCd ,memberRegDate ,memberModDate From member WHERE 1=1 AND memberDelNy = #{voDelNy}
AND memberRegDate BETWEEN #{voDateStart} AND #{voDateEnd} AND memberModDate BETWEEN #{voDateStart} AND #{voDateEnd}
AND memberSeq = #{voValue} AND memberGenderCd = #{voValue} AND memberBirthday LIKE CONCAT('%',#{voValue},'%') AND memberName LIKE CONCAT('%',#{voValue},'%') AND memberID LIKE CONCAT('%',#{voValue},'%')

mapper.update:
UPDATE member
memberEmail = #{memberEmail}, memberDpt = #{memberDpt},
where 1=1 and memberSeq=#{memberSeq}

mapper.delete:
delete From member
	where 1=1
and memberSeq=#{memberSeq}
