mapper.sql:
FROM party a
WHERE 1=1
	AND a.partyDelNy = #{voDelNy}


	AND a.partyRegDate BETWEEN #{voDateStart} AND #{voDateEnd}
	AND a.partyModDate BETWEEN #{voDateStart} AND #{voDateEnd}



	AND a.partySeq = #{voValue}
	AND a.partyName LIKE CONCAT('%',#{voValue},'%')

mapper.insert:
insert into party(
partyName
,partyOwnerKDA
,partyOwnerTierCd
,partyMatchTypeCd
,partyDept
,partyDelNy
)values(
#{partyName}
,#{partyOwnerKDA}
,#{partyOwnerTierCd}
,#{partyMatchTypeCd}
,#{partyDept}
,0
);



	select last_insert_id()

mapper.update:
UPDATE party
SET
partyName = #{partyName}
,partyDept = #{partyDept}
,partyOwnerKDA = #{partyOwnerKDA}
,partyOwnerTierCd = #{partyOwnerTierCd}
,partyMatchTypeCd = #{partyMatchTypeCd}
,partyModDate = now()
where
1=1
and partySeq = #{partySeq}

mapper.delete:
Delete from party
where 1=1
and partySeq = #{partySeq}
