,partyDept
,partyDelNy
)values(
#{partyName}
,#{partyOwnerKDA}
,#{partyOwnerTierCd}
,#{partyMatchTypeCd}
,#{partyDept}
,0
);



	select last_insert_id()

mapper.update:
UPDATE party
SET
partyName = #{partyName}
,partyDept = #{partyDept}
,partyOwnerKDA = #{partyOwnerKDA}
,partyOwnerTierCd = #{partyOwnerTierCd}
,partyMatchTypeCd = #{partyMatchTypeCd}
,partyModDate = now()
where
1=1
and partySeq = #{partySeq}

mapper.delete:
Delete from party
where 1=1
and partySeq = #{partySeq}
