mapper.sql:
FROM party a
	LEFT JOIN memberParty mp ON a.partySeq = mp.partySeqF
	LEFT JOIN member m ON m.memberSeq = mp.memberSeqF
WHERE 1=1
	AND mp.masterNy = 1
	AND a.partyDelNy = #{voDelNy}
	AND m.memberTierCd = #{voXMemberTierCd}
	AND a.partyMatchTypeCd = #{voPartyMatchTypeCd}
	AND a.partyName LIKE CONCAT('%',#{voPartyName},'%')


	AND a.partyRegDate BETWEEN #{voDateStart} AND #{voDateEnd}
	AND a.partyModDate BETWEEN #{voDateStart} AND #{voDateEnd}



	AND a.partySeq = #{voValue}
	AND a.partyName LIKE CONCAT('%',#{voValue},'%')
	AND a.partyMatchTypeCd LIKE CONCAT('%',#{voValue},'%')

mapper.insert:
insert into party(
partyName
,partyMatchTypeCd
,partyDept
,partyDelNy
)values(
#{partyName}
,#{partyMatchTypeCd}
,#{partyDept}
,0
);



	select last_insert_id()

mapper.update:
UPDATE party
SET
partyName = #{partyName}
,partyDept = #{partyDept}
,partyMatchTypeCd = #{partyMatchTypeCd}
,partyModDate = now()
where
1=1
and partySeq = #{partySeq}

mapper.delete:
Delete from party
where 1=1
and partySeq = #{partySeq}
